SELECT SUBJECT.ID,SUBJECT.NAME,SUBJECT.DESCRIPTION,SUBJECT.GRADE FROM SUBJECT INNER JOIN MARK ON SUBJECT.ID = MARK.SUBJECT_ID GROUP BY SUBJECT.ID HAVING AVG(MARK.MARK) > (SELECT AVG(MARK) FROM MARK);
SELECT S.ID, S.NAME,S.BIRTHDAY,S.GROUPNUMBER FROM STUDENT S JOIN (SELECT STUDENT_ID, SUM(AMOUNT) AS TOTAL_PAID FROM PAYMENT GROUP BY STUDENT_ID) P ON S.ID=P.STUDENT_ID WHERE P.TOTAL_PAID>(SELECT AVG(STUDENT_TOTAL) FROM (SELECT SUM(AMOUNT) AS STUDENT_TOTAL FROM PAYMENT GROUP BY P.STUDENT_ID) SUB);