SELECT SUBJECT.ID,SUBJECT.NAME,SUBJECT.DESCRIPTION,SUBJECT.GRADE FROM SUBJECT INNER JOIN MARK ON SUBJECT.ID = MARK.SUBJECT_ID GROUP BY SUBJECT.ID HAVING AVG(MARK.MARK) > (SELECT AVG(MARK) FROM MARK);
SELECT STUDENT.ID,STUDENT.NAME,STUDENT.BIRTHDAY,STUDENT.GROUPNUMBER FROM STUDENT INNER JOIN PAYMENT ON STUDENT.ID = PAYMENT.STUDENT_ID GROUP BY STUDENT.ID HAVING AVG(CAST(PAYMENT.AMOUNT AS DECIMAL(10,2))) > (SELECT AVG(CAST(AMOUNT AS DECIMAL(10,2))) FROM PAYMENT);